import { Component} from 'react';
import './App.css';

class App extends Component {
  constructor(){
    super();
    this.state = {
      monsters : [],
      searchField : "", //its a string of search input ---> empty on the start
    };

    console.log('Constructor')

  }

  componentDidMount(){

    console.log('Component Did Mount');

    fetch ('https://jsonplaceholder.typicode.com/users')
    .then((response) => response.json())
    .then((users) => this.setState(() => {
      return { monsters: users };
    },
      () => console.log(this.state))
    )
    .catch( error => console.log('I have failed')
  );}

  onSearchChange = (event) =>{
    const searchField = event.target.value.toLocaleLowerCase();
    this.setState(() => {
      return { searchField }//its the same like - { searchField : searchField}
    });
  }

  render(){

    console.log("Render")
    //a new array for monsters after filtering
    const filteredMonsters = this.state.monsters.filter((monster) => {
      return monster.name.toLocaleLowerCase().includes(this.state.searchField);
    });

    return (
      <div className="App">
        <input 
          className='search-box' 
          type='search' 
          placeholder='Search monsters' 
          onChange={this.onSearchChange}
        />
        { // in filter method we need key value , for more optimezation then the component is updating or re-rendering.
          filteredMonsters.map((monster) => {
            return <h1 key = {monster.id}> {monster.name} </h1>
          })
        }

      </div>
    );
  }
}


export default App;
